INSERT INTO conteudos(conteudos_nome, conteudos_descricao, conteudos_foto, conteudos_ordem, conteudos_del) VALUES ('historia da quimica', 'Antes da alquimia, o homem tinha uma vaga noção da química, nessa época era conhecido apenas técnicas como, destilação, fermentação, fundição de metais','../uploads/content_pics/historia-da-qumica.jpg', 1, 'N');
INSERT INTO slides(conteudos_id, slides_ordem, slides_conteudo, slides_del)VALUES (1, 1, 'Antes da alquimia, o homem tinha uma vaga noção da química, nessa época era conhecido apenas técnicas como, destilação, fermentação, fundição de metais (ouro, cobre, ferro, estanho). Esse conhecimento foi obtido por observação na natureza e apenas depois do domínio do fogo. A alquimia surgiu em meados de 300 d.C. no Egito, e se expandiu pela Europa. Embora o conhecimento obtido ao longo do tempo se sustentasse, a alquimia tinha um lado que puxava para o misticismo, os praticantes, se concentravam em obter a Pedra Filosofal e o Elixir da Longa Vida. Embora seu foco não fosse o desenvolvimento de uma ciência, os alquimistas desenvolveram e melhoraram várias técnicas utilizadas em química, tais como: produção e fusão de ligas metálicas, destilação, sublimação, calcinação, dissolução, filtração e cristalização. A química é considerada ciência a pouco mais de 300 anos, antes desse período muitos alquimistas foram considerados bruxos devido ao misticismo relacionado à alquimia, e mortos em fogueira. As guerras mundiais trouxeram grande avanço na área química, foram feitos vários investimentos para o desenvolvimento deste conhecimento, mas infelizmente de forma torpe.', 'N');
INSERT INTO perguntas(conteudos_id, perguntas_descricao, perguntas_del)VALUES (1, 'Em 1872, Robert Angus Smith criou o termo chuva ácida, descrevendo precipitações ácidas em Manchester após a Revolução Industrial. Trata-se do acúmulo demasiado de dióxido de carbono e enxofre na atmosfera que, ao reagirem com compostos dessa camada, formam gotículas de chuva ácida e partículas de aerossóis. A chuva ácida não necessariamente ocorre no local poluidor, pois tais poluentes, ao serem lançados na atmosfera, são levados pelos ventos, podendo provocar a reação em regiões distantes. A água de forma pura apresenta pH 7 e, ao contatar agentes poluidores, reage modificando seu pH para 5,6 e até menos que isso, o que provoca reações, deixando consequências. O texto aponta para um fenômeno atmosférico causador de graves problemas ao meio ambiente: a chuva ácida (pluviosidade com pH baixo). Esse fenômeno tem como consequência', 'N');
